import * as program from 'commander';
import { ProjectsCommandHandler } from '../command-handlers/projects-command-handler';

export class ProjectsCommand {

  public static install() {
    program
      .command('projects')
      .description('List projects.')
      .action(async (options) => {
        const handler = new ProjectsCommandHandler(options.parent?.token);
        await handler.run();
      });
  }
}