import * as program from 'commander';
import { SessionCommandHandler } from '../command-handlers/session-command-handler';

export class SessionCommand {

  public static install() {
    program
      .command('session')
      .description('Show current session')
      .action(async (options) => {
        const handler = new SessionCommandHandler(options.parent?.token);
        await handler.run();
      });
  }
}