import * as axios from 'axios';
import { config } from '../config/config';
import { AuthService } from './auth.service';

export class EnvironmentService {

  public constructor(private _token?: string) {
  }

  public async get(projectId: string, environment: string): Promise<any> {
    const authService = new AuthService();
    const token = authService.getTokenOrThrow(this._token);
    const url = `${config.serverBaseUrl}/projects/${projectId}/environments/${environment}`;
    const response = await axios.default.get(url,{
      headers: {
        "cli-token": token,
      }
    });
    return response.data;
  }
}